import { Component } from '@angular/core';
import { Footballer } from './models/footballers';
import { FootballersService } from './services/footballers.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'Footballers.UI';

  footballers: Footballer[] = [];
  footballerToEdit?: Footballer;

  constructor(private footballersService: FootballersService) {}

  ngOnInit(): void {
    this.footballersService
      .getFootballers()
        .subscribe((result: Footballer[]) => (this.footballers = result));
  }

  initNewFootballer() {
    this.footballerToEdit = new Footballer();
  }

  editFootballer(footballer: Footballer) {
    this.footballerToEdit = footballer;
  }

  updateFootballerList(footballers: Footballer[]){
    this.footballers = footballers;
  }
}
<div class="app-container">
  <button class="create-footballer-btn" (click)="initNewFootballer()">Create new footballer</button>

  <table class="footballers-list">
      <thead>
          <th>First Name</th>
          <th>Last Name</th>
          <th>Number of goals</th>
          <th>Team</th>
          <th></th>
      </thead>
      <tbody>
          <tr *ngFor="let footballer of footballers">
              <td>{{ footballer.firstName }}</td>
              <td>{{ footballer.lastName }}</td>
              <td>{{ footballer.goals }}</td>
              <td>{{ footballer.team }}</td>
              <td><button (click)="editFootballer(footballer)">Edit</button></td>
          </tr>
      </tbody>
  </table>

  <app-edit-footballer [footballer]="footballerToEdit" (footballersUpdated)="updateFootballerList($event)"></app-edit-footballer>
</div>
